import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import { useSearchParams } from 'react-router-dom';
import { productService } from '../services/productService';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';

const Menu: React.FC = () => {
    const [searchParams, setSearchParams] = useSearchParams();
    const activeCategory = searchParams.get('category') || 'all';
    const searchQuery = searchParams.get('search') || '';

    const [menuItems, setMenuItems] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [addingId, setAddingId] = useState<number | null>(null);
    const { addToCart } = useCart();
    const { user } = useAuth();

    useEffect(() => {
        loadProducts();
    }, [activeCategory, searchQuery]);

    const loadProducts = async () => {
        setLoading(true);
        try {
            const products = await productService.getProducts(activeCategory, searchQuery);
            setMenuItems(products);
        } catch (error) {
            console.error('Failed to load products:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleCategoryChange = (categoryId: string) => {
        const params = new URLSearchParams(searchParams);
        if (categoryId === 'all') {
            params.delete('category');
        } else {
            params.set('category', categoryId);
        }
        setSearchParams(params);
    };

    const handleAddToCart = async (e: React.MouseEvent, productId: number) => {
        e.preventDefault();
        if (!user) {
            toast.error('Please login to add items to cart');
            return;
        }

        setAddingId(productId);
        try {
            await addToCart(productId, 1);
            toast.success('Item added to cart successfully!');
        } catch (error: any) {
            console.error('Failed to add to cart:', error);
            const msg = error.response?.data?.detail || error.message || "Failed to add to cart";
            toast.error(`Error: ${msg}`);
        } finally {
            setAddingId(null);
        }
    };

    const categories = [
        { id: 'all', name: 'All Items', emoji: 'üçΩÔ∏è' },
        { id: 'appetizers', name: 'Appetizers', emoji: 'ü•ó' },
        { id: 'main', name: 'Main Courses', emoji: 'üçñ' },
        { id: 'pasta', name: 'Pasta & Pizza', emoji: 'üçù' },
        { id: 'seafood', name: 'Seafood', emoji: 'ü¶û' },
        { id: 'desserts', name: 'Desserts', emoji: 'üç∞' },
        { id: 'beverages', name: 'Beverages', emoji: 'ü•§' }
    ];

    if (loading) {
        return (
            <div className="container py-5">
                <div className="text-center">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                    <p className="mt-3">Loading menu...</p>
                </div>
            </div>
        );
    }

    const filteredItems = menuItems;

    return (
        <div style={{ background: 'linear-gradient(to bottom, #FFF5F0 0%, #FFFFFF 100%)', minHeight: '100vh' }}>
            <div className="container py-5">
                {/* Header */}
                <div className="text-center mb-5">
                    <div className="d-inline-flex align-items-center bg-light px-3 py-2 rounded-pill mb-3">
                        <i className="ci-restaurant text-danger me-2"></i>
                        <span className="text-uppercase fw-semibold" style={{ fontSize: '12px', letterSpacing: '1px', color: '#FF6B35' }}>
                            FRESH MENU
                        </span>
                    </div>
                    <h1 className="display-4 fw-bold mb-3">Our Menu</h1>
                    <p className="lead text-muted mb-4" style={{ maxWidth: '600px', margin: '0 auto' }}>
                        Discover our delicious selection of fresh dishes, prepared with love and served with passion
                    </p>
                </div>

                {/* Category Tabs */}
                <div className="text-center mb-5">
                    <div className="d-inline-flex flex-wrap justify-content-center bg-white rounded-pill p-1 shadow-sm" style={{ gap: '8px' }}>
                        {categories.map((category) => (
                            <button
                                key={category.id}
                                className={`btn rounded-pill px-4 py-2 fw-semibold transition-all ${activeCategory === category.id
                                    ? 'btn-primary'
                                    : 'btn-outline-secondary'
                                    }`}
                                style={{
                                    fontSize: '14px',
                                    borderRadius: '20px',
                                    border: 'none',
                                    transition: 'all 0.3s ease'
                                }}
                                onClick={() => handleCategoryChange(category.id)}
                            >
                                <span className="me-2">{category.emoji}</span>
                                {category.name}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Menu Items Grid */}
                <div className="row g-4">
                    {filteredItems.map((item, index) => (
                        <div key={item.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
                            <div className="card h-100 border-0 shadow-sm" style={{
                                borderRadius: '15px',
                                overflow: 'hidden',
                                transition: 'all 0.3s ease'
                            }}>
                                <div className="position-relative">
                                    <img
                                        src={item.image_url || item.image || 'https://via.placeholder.com/300x200/cccccc/666666?text=No+Image'}
                                        alt={item.name}
                                        className="card-img-top"
                                        style={{
                                            height: '200px',
                                            objectFit: 'cover',
                                            transition: 'transform 0.3s ease'
                                        }}
                                    />
                                </div>

                                <div className="card-body d-flex flex-column">
                                    <h3 className="card-title h5 mb-3" style={{
                                        fontSize: '16px',
                                        fontWeight: '600',
                                        color: '#2d3436'
                                    }}>
                                        {item.name}
                                    </h3>

                                    <p className="small mb-3" style={{ color: '#636e72', fontSize: '0.85rem', lineHeight: '1.5' }}>
                                        {item.description || 'Delicious dish prepared with fresh ingredients'}
                                    </p>

                                    <div className="d-flex align-items-center justify-content-between mb-3">
                                        <span className="fs-xs" style={{ color: '#b2bec3' }}>
                                            {item.rating || 4.5} ({item.reviews || 50} reviews)
                                        </span>
                                        <span className="badge bg-success fs-xs" style={{ fontSize: '10px' }}>
                                            <i className="ci-package me-1"></i>
                                            {item.stock_quantity || 50} in stock
                                        </span>
                                    </div>

                                    <button
                                        className="btn w-100 text-white fw-semibold"
                                        style={{
                                            background: 'linear-gradient(135deg, #FF6B35 0%, #F7931E 100%)',
                                            border: 'none',
                                            borderRadius: '10px',
                                            padding: '10px',
                                            transition: 'all 0.3s ease'
                                        }}
                                        disabled={addingId === item.id}
                                        onClick={(e) => handleAddToCart(e, item.id)}
                                    >
                                        {addingId === item.id ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                                                Adding...
                                            </>
                                        ) : (
                                            <>
                                                <i className="ci-cart me-2"></i>
                                                Add to Cart
                                            </>
                                        )}
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Empty State */}
                {filteredItems.length === 0 && (
                    <div className="text-center py-5">
                        <div className="mb-4" style={{ fontSize: '4rem' }}>üçΩÔ∏è</div>
                        <h3 className="mb-3" style={{ color: '#2d3436' }}>No items found</h3>
                        <p style={{ color: '#636e72' }}>Try selecting a different category</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Menu;
