import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import { bankService, BankAccount } from '../services/bankService';
import { nigerianStates } from '../data/nigerianStates';

const Checkout: React.FC = () => {
    const { cart, refreshCart } = useCart();
    const items = cart?.items || [];
    const total = cart?.subtotal || 0;
    const { user } = useAuth();
    const token = localStorage.getItem('access_token');
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [step, setStep] = useState(1); // 1: Details, 2: Payment, 3: Confirmation

    const [formData, setFormData] = useState({
        street: '',
        city: '',
        state: '',
        phone: '',
        notes: ''
    });

    const [bankAccounts, setBankAccounts] = useState<BankAccount[]>([]);
    const [selectedBankId, setSelectedBankId] = useState<number | null>(null);
    const [showSuccessModal, setShowSuccessModal] = useState(false);
    const [currentOrderId, setCurrentOrderId] = useState<number | null>(null);

    useEffect(() => {
        const loadBanks = async () => {
            try {
                const banks = await bankService.getActiveBanks();
                setBankAccounts(banks);
            } catch (err) {
                console.error("Failed to load banks", err);
                toast.error("Failed to load available banks");
            }
        };
        loadBanks();
    }, []);

    useEffect(() => {
        if (!user) {
            navigate('/signin', { state: { message: 'Please sign in to checkout.' } });
        } else if (items.length === 0) {
            navigate('/cart');
        }
    }, [user, items, navigate]);

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const validatePhone = (phone: string) => {
        // Basic Nigeria phone validation: starts with 0 or +234, 10-14 digits
        return /^(0|\+234)\d{9,11}$/.test(phone);
    };

    const handlePlaceOrder = async () => {
        if (!selectedBankId) {
            toast.error("Please select a bank to make payment to.");
            return;
        }

        setLoading(true);
        try {
            const orderData = {
                delivery_address: {
                    street: formData.street,
                    city: formData.city,
                    state: formData.state,
                    zip_code: "000000", // Default for now
                    country: "Nigeria", // Fixed
                    phone: formData.phone // Added to notes or separate field if backend supports
                },
                payment_method: 'bank_transfer',
                notes: `Phone: ${formData.phone}. ${formData.notes}` // Appending phone to notes for now as backend might strict
            };

            const response = await axios.post('http://localhost:8000/api/checkout/place-order', orderData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            await refreshCart();
            setCurrentOrderId(response.data.id);
            setShowSuccessModal(true);
        } catch (error: any) {
            console.error('Checkout error:', error);
            const msg = error.response?.data?.detail || "Failed to place order. Please try again.";
            toast.error(msg);
        } finally {
            setLoading(false);
        }
    };

    const handlePaymentConfirmation = async () => {
        if (!currentOrderId) return;
        try {
            await axios.post(`http://localhost:8000/api/orders/${currentOrderId}/mark-paid`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });
            toast.success("Payment marked! We will verify it shortly.");
            navigate('/dashboard');
        } catch (e) {
            console.error(e);
            toast.error("Failed to mark payment. Please contact support.");
        }
    };

    const moveStep = (nextStep: number) => {
        if (nextStep === 2) {
            if (!formData.street || !formData.city || !formData.state || !formData.phone) {
                toast.error("Please fill in all address details.");
                return;
            }
            if (!validatePhone(formData.phone)) {
                toast.error("Please enter a valid Nigerian phone number.");
                return;
            }
        }
        setStep(nextStep);
    };

    const selectedBank = bankAccounts.find(b => b.id === selectedBankId);

    // Styles
    const containerStyle = {
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 100%)',
        minHeight: '100vh',
        color: '#fff',
        fontFamily: "'Inter', sans-serif"
    };

    const glassCardStyle = {
        background: 'rgba(255, 255, 255, 0.05)',
        backdropFilter: 'blur(10px)',
        borderRadius: '16px',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        padding: '2rem'
    };

    const inputStyle = {
        background: 'rgba(0, 0, 0, 0.2)',
        border: '1px solid rgba(255, 255, 255, 0.1)',
        color: '#fff',
        borderRadius: '8px',
        padding: '12px'
    };

    if (items.length === 0) return null;

    return (
        <div style={containerStyle} className="py-5">
            <div className="container">
                <div className="row g-5">
                    {/* Left Column: Flow */}
                    <div className="col-lg-8">
                        {/* Progress */}
                        <div className="d-flex align-items-center mb-5 px-3">
                            <div className={`d-flex align-items-center justify-content-center rounded-circle ${step >= 1 ? 'bg-primary text-white' : 'bg-secondary'}`} style={{ width: 40, height: 40 }}>1</div>
                            <div className="mx-2 flex-grow-1" style={{ height: 2, background: 'rgba(255,255,255,0.1)' }}></div>
                            <div className={`d-flex align-items-center justify-content-center rounded-circle ${step >= 2 ? 'bg-primary text-white' : 'bg-secondary'}`} style={{ width: 40, height: 40 }}>2</div>
                            <div className="mx-2 flex-grow-1" style={{ height: 2, background: 'rgba(255,255,255,0.1)' }}></div>
                            <div className={`d-flex align-items-center justify-content-center rounded-circle ${step >= 3 ? 'bg-primary text-white' : 'bg-secondary'}`} style={{ width: 40, height: 40 }}>3</div>
                        </div>

                        <div style={glassCardStyle}>
                            {step === 1 && (
                                <div className="animate__animated animate__fadeIn">
                                    <h3 className="mb-4">Delivery Details</h3>
                                    <div className="row g-3">
                                        <div className="col-12">
                                            <label className="form-label text-muted small">Street Address</label>
                                            <input type="text" className="form-control" style={inputStyle} name="street" value={formData.street} onChange={handleInputChange} placeholder="e.g. 12 Adeola Odeku St" />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label text-muted small">City</label>
                                            <input type="text" className="form-control" style={inputStyle} name="city" value={formData.city} onChange={handleInputChange} placeholder="e.g. Victoria Island" />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="form-label text-muted small">State</label>
                                            <select className="form-select" style={inputStyle} name="state" value={formData.state} onChange={handleInputChange}>
                                                <option value="" style={{ color: '#000' }}>Select State</option>
                                                {nigerianStates.map(s => (
                                                    <option key={s} value={s} style={{ color: '#000' }}>{s}</option>
                                                ))}
                                            </select>
                                        </div>
                                        <div className="col-md-12">
                                            <label className="form-label text-muted small">Phone Number</label>
                                            <input type="tel" className="form-control" style={inputStyle} name="phone" value={formData.phone} onChange={handleInputChange} placeholder="e.g. 08012345678" />
                                        </div>
                                        <div className="col-12 mt-4">
                                            <button className="btn btn-primary w-100 py-3 rounded-pill fw-bold" onClick={() => moveStep(2)}>
                                                Continue to Payment
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {step === 2 && (
                                <div className="animate__animated animate__fadeIn">
                                    <h3 className="mb-4">Select Bank for Transfer</h3>
                                    <p className="text-secondary mb-4">You will make a direct transfer to the selected account.</p>

                                    <div className="row g-3">
                                        {bankAccounts.map(bank => (
                                            <div className="col-md-6" key={bank.id}>
                                                <div
                                                    onClick={() => setSelectedBankId(bank.id)}
                                                    style={{
                                                        ...glassCardStyle,
                                                        background: selectedBankId === bank.id ? 'rgba(13, 110, 253, 0.2)' : 'rgba(255,255,255,0.05)',
                                                        border: selectedBankId === bank.id ? '1px solid #0d6efd' : '1px solid rgba(255,255,255,0.1)',
                                                        cursor: 'pointer',
                                                        padding: '1.5rem',
                                                        transition: 'all 0.3s ease'
                                                    }}
                                                    className="h-100 d-flex flex-column justify-content-center align-items-center text-center"
                                                >
                                                    <div className="mb-2">
                                                        <i className="bi bi-bank fs-2 text-primary"></i>
                                                    </div>
                                                    <h5 className="mb-1">{bank.bank_name}</h5>
                                                    <span className="badge bg-light text-dark">{bank.account_name}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>

                                    {bankAccounts.length === 0 && (
                                        <div className="alert alert-warning mt-3">No active bank accounts available. Please contact support.</div>
                                    )}

                                    <div className="mt-4 pt-3 border-top border-secondary d-flex justify-content-between">
                                        <button className="btn btn-outline-light rounded-pill px-4" onClick={() => setStep(1)}>Back</button>
                                        <button className="btn btn-primary rounded-pill px-5 fw-bold" onClick={() => moveStep(3)} disabled={!selectedBankId}>
                                            Review & Pay
                                        </button>
                                    </div>
                                </div>
                            )}

                            {step === 3 && (
                                <div className="animate__animated animate__fadeIn">
                                    <h3 className="mb-4">Review Order</h3>

                                    <div className="mb-4 p-3 rounded" style={{ background: 'rgba(0,0,0,0.2)' }}>
                                        <h6 className="text-muted text-uppercase small ls-1">Delivery To</h6>
                                        <p className="mb-0 fs-5">{formData.street}, {formData.city}, {formData.street}</p>
                                        <p className="mb-0 text-primary">{formData.phone}</p>
                                    </div>

                                    <div className="mb-4 p-3 rounded" style={{ background: 'rgba(0,0,0,0.2)' }}>
                                        <h6 className="text-muted text-uppercase small ls-1">Payment Method</h6>
                                        <div className="d-flex align-items-center mt-2">
                                            <i className="bi bi-bank2 me-2 fs-5"></i>
                                            <div>
                                                <div className="fw-bold">Bank Transfer</div>
                                                <div className="small text-muted">To: {selectedBank?.bank_name}</div>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="mt-4 pt-3 border-top border-secondary d-flex justify-content-between">
                                        <button className="btn btn-outline-light rounded-pill px-4" onClick={() => setStep(2)}>Back</button>
                                        <button
                                            className="btn btn-success rounded-pill px-5 fw-bold shadow-lg"
                                            onClick={handlePlaceOrder}
                                            disabled={loading}
                                            style={{ transform: 'scale(1.05)' }}
                                        >
                                            {loading ? <span className="spinner-border spinner-border-sm me-2"></span> : <i className="bi bi-lock-fill me-2"></i>}
                                            Pay ₦{total.toFixed(2)}
                                        </button>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Right Column: Summary */}
                    <div className="col-lg-4">
                        <div style={glassCardStyle}>
                            <h5 className="mb-4 border-bottom border-secondary pb-3">Order Summary</h5>
                            {items.map(item => (
                                <div key={item.id} className="d-flex align-items-center mb-3">
                                    <div
                                        style={{
                                            width: 50,
                                            height: 50,
                                            borderRadius: 8,
                                            background: '#333',
                                            backgroundImage: `url(${item.product?.image_url})`,
                                            backgroundSize: 'cover'
                                        }}
                                        className="me-3"
                                    ></div>
                                    <div className="flex-grow-1">
                                        <div className="fw-bold small">{item.product?.name}</div>
                                        <div className="text-muted small">x{item.quantity}</div>
                                    </div>
                                    <div className="fw-bold">₦{(item.price_at_addition * item.quantity).toFixed(2)}</div>
                                </div>
                            ))}

                            <div className="mt-4 pt-3 border-top border-secondary">
                                <div className="d-flex justify-content-between mb-2">
                                    <span className="text-muted">Subtotal</span>
                                    <span>₦{total.toFixed(2)}</span>
                                </div>
                                <div className="d-flex justify-content-between mb-2">
                                    <span className="text-muted">Delivery</span>
                                    <span className="text-success">Free</span>
                                </div>
                                <div className="d-flex justify-content-between fs-4 fw-bold mt-3">
                                    <span>Total</span>
                                    <span>₦{total.toFixed(2)}</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Payment Modal */}
            {showSuccessModal && selectedBank && (
                <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.8)', backdropFilter: 'blur(5px)' }}>
                    <div className="modal-dialog modal-dialog-centered">
                        <div className="modal-content border-0 shadow-lg" style={{ borderRadius: 20, overflow: 'hidden' }}>
                            <div className="modal-header bg-success text-white border-0 py-4 justify-content-center flex-column align-items-center">
                                <div className="bg-white rounded-circle d-flex align-items-center justify-content-center mb-3" style={{ width: 60, height: 60 }}>
                                    <i className="bi bi-check-lg text-success fs-2"></i>
                                </div>
                                <h4 className="modal-title fw-bold">Order Placed!</h4>
                                <p className="mb-0 opacity-75">Order ID: #{currentOrderId}</p>
                            </div>
                            <div className="modal-body p-4 text-center bg-light">
                                <p className="text-muted mb-4">Please complete your payment by transferring <strong>₦{total.toFixed(2)}</strong> to the account below:</p>

                                <div className="card border-primary border-2 mb-4 bg-white shadow-sm">
                                    <div className="card-body">
                                        <h3 className="text-primary fw-bold mb-1">{selectedBank.bank_name}</h3>
                                        <div className="display-6 fw-bold mb-2 text-dark" style={{ letterSpacing: 2 }}>{selectedBank.account_number}</div>
                                        <div className="text-muted text-uppercase small fw-bold">{selectedBank.account_name}</div>
                                    </div>
                                </div>

                                <div className="alert alert-info small text-start">
                                    <i className="bi bi-info-circle-fill me-2"></i>
                                    Use <strong>Reference / Remark: Order #{currentOrderId}</strong> when making the transfer.
                                </div>

                                <button
                                    className="btn btn-dark w-100 py-3 rounded-pill fw-bold"
                                    onClick={handlePaymentConfirmation}
                                >
                                    I Have Made The Payment
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Checkout;
